$(document).ready(function(){$.fn.displayMessaging=function(){$el=$(this);$el.animate({top:'0px'},300,'ease-out');window.setTimeout(function(){$el.animate({top:'-70px'},300,'ease-out')},5000)};if($('#notification').length){$('#notification').submit(function(e){$.ajax({type:'POST',url:'/startstep/test.php',data:$(this).serialize(),dataType:'html',timeout:5000,success:function(){$('.message-success').displayMessaging()},error:function(){$('.message-error').displayMessaging()}});e.preventDefault()})}});